import React, { useState, useRef, useEffect } from 'react';
import { Play, Pause, SkipBack, SkipForward, Search, Music } from 'lucide-react';
import song1 from '../assets/music/1.mp3';
import song2 from '../assets/music/2.mp3';
import song3 from '../assets/music/3.mp3';

const bhajansDatabase = [
  { id: 1, title: "ॐ जय जगदीश हरे", artist: "Traditional", audioUrl: song1, duration: 300, lyrics: ["ॐ जय जगदीश हरे", "स्वामी जय जगदीश हरे", "भक्त जनों के संकट, दास जनों के संकट", "क्षण में दूर करे॥"] },
  { id: 2, title: "हनुमान चालीसा", artist: "Traditional", audioUrl: song2, duration: 480, lyrics: ["श्री गुरु चरण सरोज रज", "निज मनु मुकुरु सुधारि", "बरनऊं रघुबर बिमल जसु", "जो दायकु फल चारि"] },
  { id: 3, title: "गायत्री मंत्र", artist: "Traditional", audioUrl: song3, duration: 240, lyrics: ["ॐ भूर्भुवः स्वः", "तत्सवितुर्वरेण्यं", "भर्गो देवस्य धीमहि", "धियो यो नः प्रचोदयात्"] }
];

const SpiritualMusicPlayer = () => {
  const [isPlaying, setIsPlaying] = useState(false);
  const [currentTime, setCurrentTime] = useState(0);
  const [songs, setSongs] = useState(bhajansDatabase);
  const [currentSongIndex, setCurrentSongIndex] = useState(0);
  const [searchQuery, setSearchQuery] = useState('');
  const [currentLyricIndex, setCurrentLyricIndex] = useState(0);
  const audioRef = useRef(null);

  useEffect(() => {
    audioRef.current = new Audio();
    audioRef.current.src = songs[currentSongIndex].audioUrl;
    return () => {
      if (audioRef.current) {
        audioRef.current.pause();
        audioRef.current = null;
      }
    };
  }, [songs, currentSongIndex]);

  const currentSong = songs[currentSongIndex];

  const handleSearch = (query) => {
    setSearchQuery(query);
    if (query.trim() === '') {
      setSongs(bhajansDatabase);
      setCurrentSongIndex(0);
    } else {
      const filtered = bhajansDatabase.filter(song => 
        song.title.toLowerCase().includes(query.toLowerCase()) ||
        song.artist.toLowerCase().includes(query.toLowerCase())
      );
      setSongs(filtered);
      setCurrentSongIndex(0);
    }
  };

  useEffect(() => {
    const audio = audioRef.current;
    if (!audio) return;

    const handleTimeUpdate = () => {
      setCurrentTime(audio.currentTime);
      const lyricIndex = Math.floor(audio.currentTime / 5) % currentSong.lyrics.length;
      setCurrentLyricIndex(lyricIndex);
    };

    audio.addEventListener('timeupdate', handleTimeUpdate);
    return () => audio.removeEventListener('timeupdate', handleTimeUpdate);
  }, [currentSong]);

  useEffect(() => {
    const audio = audioRef.current;
    if (!audio) return;
    if (isPlaying) {
      audio.play().catch(error => console.log("Playback failed:", error));
    } else {
      audio.pause();
    }
  }, [isPlaying]);

  useEffect(() => {
    const audio = audioRef.current;
    if (!audio) return;
    const wasPlaying = !audio.paused;
    audio.src = currentSong.audioUrl;
    audio.load();
    if (wasPlaying) {
      audio.play().catch(error => console.log("Playback failed:", error));
    } else {
      setIsPlaying(false);
    }
    setCurrentTime(0);
    setCurrentLyricIndex(0);
  }, [currentSongIndex, currentSong]);

  const handleNext = () => setCurrentSongIndex((prev) => (prev + 1) % songs.length);
  const handlePrevious = () => setCurrentSongIndex((prev) => (prev - 1 + songs.length) % songs.length);

  const handleSeek = (e) => {
    const audio = audioRef.current;
    if (!audio) return;
    const rect = e.target.getBoundingClientRect();
    const x = e.clientX - rect.left;
    const percentage = x / rect.width;
    const newTime = percentage * currentSong.duration;
    audio.currentTime = newTime;
    setCurrentTime(newTime);
  };

  return (
    <div className="max-w-4xl mx-auto p-6 bg-blue-50 min-h-screen">
      <div className="mb-8">
        <h1 className="text-3xl font-bold text-blue-800 mb-4">भजन प्लेयर</h1>
        <div className="flex gap-4">
          <input type="text" value={searchQuery} onChange={(e) => handleSearch(e.target.value)} placeholder="भजन खोजें..." className="flex-1 text-lg p-6 border border-blue-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500" />
          <button className="bg-blue-600 text-white p-4 rounded-lg hover:bg-blue-700 transition-colors">
            <Search size={24} />
          </button>
        </div>
      </div>

      <div className="bg-white rounded-xl shadow-xl p-6">
        <div className="flex items-center gap-4 mb-6">
          <div className="bg-blue-100 p-4 rounded-full">
            <Music className="w-12 h-12 text-blue-600" />
          </div>
          <div>
            <h2 className="text-2xl font-bold text-blue-800">{currentSong.title}</h2>
            <p className="text-blue-600">{currentSong.artist}</p>
          </div>
        </div>

        <div className="mb-6">
          <div className="relative h-3 bg-blue-100 rounded-full cursor-pointer" onClick={handleSeek}>
            <div className="absolute top-0 left-0 h-full bg-blue-600 rounded-full transition-all duration-300 ease-in-out" style={{ width: `${(currentTime / currentSong.duration) * 100}%` }} />
          </div>
          <div className="flex justify-between mt-2 text-blue-600 font-medium">
            <span>{Math.floor(currentTime / 60)}:{String(Math.floor(currentTime % 60)).padStart(2, '0')}</span>
            <span>{Math.floor(currentSong.duration / 60)}:{String(currentSong.duration % 60).padStart(2, '0')}</span>
          </div>
        </div>
      </div>
    </div>
  );
};

export default SpiritualMusicPlayer;
