import React, { useState, useRef, useEffect } from 'react';
import { Play, Pause, SkipBack, SkipForward, Search, Music } from 'lucide-react';
import song1 from '../assets/music/1.mp3';
import song2 from '../assets/music/2.mp3';
import song3 from '../assets/music/3.mp3';

const bhajansDatabase = [
  // ... (same as before)
];

const SpiritualMusicPlayer = () => {
  // ... (same state and logic as before)

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-900 to-blue-700 p-8">
      <div className="max-w-7xl mx-auto grid grid-cols-1 lg:grid-cols-2 gap-8">
        {/* Left Column: Music Player */}
        <div className="bg-blue-800 rounded-xl shadow-2xl p-6">
          {/* Search Section */}
          <div className="mb-8">
            <h1 className="text-3xl font-bold text-white mb-4">भजन प्लेयर</h1>
            <div className="flex gap-4">
              <input
                type="text"
                value={searchQuery}
                onChange={(e) => handleSearch(e.target.value)}
                placeholder="भजन खोजें..."
                className="flex-1 text-lg p-4 border border-blue-500 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-400 bg-blue-700 text-white placeholder-blue-300"
              />
              <button className="bg-blue-500 text-white p-4 rounded-lg hover:bg-blue-600 transition-colors">
                <Search size={24} />
              </button>
            </div>
          </div>

          {/* Song Info */}
          <div className="flex items-center gap-4 mb-6">
            <div className="bg-blue-600 p-4 rounded-full">
              <Music className="w-12 h-12 text-white" />
            </div>
            <div>
              <h2 className="text-2xl font-bold text-white">{currentSong.title}</h2>
              <p className="text-blue-200">{currentSong.artist}</p>
            </div>
          </div>

          {/* Progress Bar */}
          <div className="mb-6">
            <div
              className="relative h-3 bg-blue-700 rounded-full cursor-pointer"
              onClick={handleSeek}
            >
              <div
                className="absolute top-0 left-0 h-full bg-blue-400 rounded-full transition-all duration-300 ease-in-out"
                style={{ width: `${(currentTime / currentSong.duration) * 100}%` }}
              />
            </div>
            <div className="flex justify-between mt-2 text-blue-300 font-medium">
              <span>{Math.floor(currentTime / 60)}:{String(Math.floor(currentTime % 60)).padStart(2, '0')}</span>
              <span>{Math.floor(currentSong.duration / 60)}:{String(currentSong.duration % 60).padStart(2, '0')}</span>
            </div>
          </div>

          {/* Controls */}
          <div className="flex justify-center items-center gap-8">
            <button
              className="p-3 text-blue-300 hover:text-white transition-colors"
              onClick={handlePrevious}
            >
              <SkipBack size={36} />
            </button>
            <button
              className="p-4 bg-blue-500 text-white rounded-full hover:bg-blue-600 transition-colors transform hover:scale-105"
              onClick={() => setIsPlaying(!isPlaying)}
            >
              {isPlaying ? <Pause size={48} /> : <Play size={48} />}
            </button>
            <button
              className="p-3 text-blue-300 hover:text-white transition-colors"
              onClick={handleNext}
            >
              <SkipForward size={36} />
            </button>
          </div>

          {/* Playlist Section */}
          <div className="mt-6 border-t border-blue-700 pt-4">
            <h3 className="text-lg font-semibold text-white mb-2">सभी भजन:</h3>
            <div className="space-y-2">
              {songs.map((song, index) => (
                <div
                  key={song.id}
                  onClick={() => setCurrentSongIndex(index)}
                  className={`flex items-center p-2 rounded-lg cursor-pointer ${
                    currentSongIndex === index ? 'bg-blue-700' : 'hover:bg-blue-600'
                  }`}
                >
                  <Music className="w-5 h-5 text-blue-300 mr-3" />
                  <span className="text-blue-100">{song.title}</span>
                </div>
              ))}
            </div>
          </div>
        </div>

        {/* Right Column: Lyrics */}
        <div className="bg-blue-800 rounded-xl shadow-2xl p-6">
          <h3 className="text-xl font-semibold text-white mb-4">बोल:</h3>
          <div
            ref={lyricsContainerRef}
            className="space-y-4 h-[400px] overflow-y-auto scroll-smooth"
          >
            {displayedLyrics.map((line, index) => (
              <p
                key={startIndex + index}
                className={`text-lg leading-relaxed p-4 rounded-lg transition-all duration-300 ${
                  startIndex + index === currentLyricIndex
                    ? 'bg-gradient-to-r from-blue-600 to-blue-500 text-white shadow-md'
                    : 'text-blue-200 hover:bg-blue-700'
                }`}
              >
                {line.text}
              </p>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default SpiritualMusicPlayer;
