import React, { useState, useEffect } from 'react';
import { Clock, MapPin, Menu, Sun, Music, BookOpen, Leaf, Video, Mail, X, ChevronDown, ChevronUp } from 'lucide-react';
import { Link } from 'react-router-dom';

const SpiritualWellnessPage = () => {
  // State for mobile menu
  const [mobileMenuOpen, setMobileMenuOpen] = useState(false);
  // State for scroll position (for animations)
  const [scrollPosition, setScrollPosition] = useState(0);
  // State for featured event countdown
  const [timeRemaining, setTimeRemaining] = useState({
    days: 0,
    hours: 0,
    minutes: 0,
    seconds: 0
  });
  // State for testimonial carousel
  const [activeTestimonial, setActiveTestimonial] = useState(0);
  // State for accordion in mobile view
  const [openAccordion, setOpenAccordion] = useState(null);
  // State for animation of cards
  const [animatedCards, setAnimatedCards] = useState([]);

  // FAQ accordion data
  const faqItems = [
    {
      question: "What is Dhyana's approach to spiritual wellness?",
      answer: "Dhyana combines ancient Indian wisdom with modern wellness practices to create a holistic approach to spiritual health, focusing on mind-body balance through meditation, music, stories, and community."
    },
    {
      question: "Do I need prior experience with meditation?",
      answer: "Not at all! Our sessions cater to all levels, from beginners to advanced practitioners. We provide detailed guidance for those just starting their spiritual journey."
    },
    {
      question: "How can I access the online sessions?",
      answer: "Once registered, you'll receive a secure link via email to join our live sessions. All you need is a reliable internet connection and a quiet space."
    }
  ];

  // Testimonials data
  const testimonials = [
    {
      initial: "A",
      name: "Ananya R.",
      location: "Mumbai",
      text: "The guided meditation sessions have transformed my daily routine. I've found a sense of inner calm that helps me navigate my busy life with grace."
    },
    {
      initial: "R",
      name: "Rajesh K.",
      location: "Bangalore",
      text: "The Ayurvedic wellness tips have helped me address chronic issues that modern medicine couldn't solve. I feel more balanced and energetic."
    },
    {
      initial: "P",
      name: "Priya S.",
      location: "Delhi",
      text: "I've been listening to the bhajan playlist during my morning routine. It sets a peaceful tone for my entire day and connects me to my cultural roots."
    },
    {
      initial: "V",
      name: "Vikram J.",
      location: "Chennai",
      text: "The spiritual stories section has been eye-opening. These ancient tales provide timeless wisdom that I can apply to modern challenges."
    }
  ];

  // Handle scroll for animations
  useEffect(() => {
    const handleScroll = () => {
      setScrollPosition(window.scrollY);
      
      // Check which cards should be animated based on scroll position
      const cards = document.querySelectorAll('.offering-card');
      const newAnimatedCards = [];
      
      cards.forEach((card, index) => {
        const rect = card.getBoundingClientRect();
        if (rect.top < window.innerHeight * 0.8) {
          newAnimatedCards.push(index);
        }
      });
      
      setAnimatedCards(newAnimatedCards);
    };

    window.addEventListener('scroll', handleScroll);
    handleScroll(); // Initial check
    
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  // Event countdown timer
  useEffect(() => {
    // Set event date (April 12th, 2025 at 7:00 PM IST)
    const eventDate = new Date('2025-04-12T13:30:00Z'); // 7:00 PM IST in UTC
    
    const timer = setInterval(() => {
      const now = new Date();
      const difference = eventDate - now;
      
      if (difference > 0) {
        setTimeRemaining({
          days: Math.floor(difference / (1000 * 60 * 60 * 24)),
          hours: Math.floor((difference % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60)),
          minutes: Math.floor((difference % (1000 * 60 * 60)) / (1000 * 60)),
          seconds: Math.floor((difference % (1000 * 60)) / 1000)
        });
      }
    }, 1000);
    
    return () => clearInterval(timer);
  }, []);

  // Testimonial carousel autoplay
  useEffect(() => {
    const interval = setInterval(() => {
      setActiveTestimonial((prev) => (prev + 1) % testimonials.length);
    }, 5000);
    
    return () => clearInterval(interval);
  }, [testimonials.length]);

  // Toggle FAQ accordion
  const toggleAccordion = (index) => {
    setOpenAccordion(openAccordion === index ? null : index);
  };

  return (
    <div className="bg-slate-50 min-h-screen font-sans">
      {/* Navbar */}
      <nav className="bg-blue-900 shadow-lg fixed w-full z-10">
        <div className="max-w-6xl mx-auto px-4">
          <div className="flex justify-between">
            <div className="flex space-x-4">
              {/* Logo */}
              <div className="flex items-center py-4">
                <Sun className={`h-6 w-6 mr-2 text-white ${scrollPosition > 100 ? 'animate-pulse' : ''}`} />
                <span className="font-bold text-white text-xl">Dhyana</span>
              </div>
              
              {/* Nav Links */}
              <div className="hidden md:flex items-center space-x-1">
                <a href="#" className="py-4 px-3 text-blue-100 hover:text-white border-b-2 border-transparent hover:border-white transition-all duration-300">Home</a>
                <a href="#" className="py-4 px-3 text-blue-100 hover:text-white border-b-2 border-transparent hover:border-white transition-all duration-300">About</a>
                <a href="#" className="py-4 px-3 text-blue-100 hover:text-white border-b-2 border-transparent hover:border-white transition-all duration-300">Contact</a>
                <a href="#faq" className="py-4 px-3 text-blue-100 hover:text-white border-b-2 border-transparent hover:border-white transition-all duration-300">FAQ</a>
              </div>
            </div>
            
            {/* Secondary Nav Links */}
            <div className="hidden md:flex items-center space-x-1">
              <a href="#" className="py-2 px-3 bg-blue-700 hover:bg-blue-800 text-white rounded transition duration-300 transform hover:scale-105">Login</a>
              <a href="#" className="py-2 px-3 bg-blue-700 hover:bg-blue-800 text-white rounded transition duration-300 transform hover:scale-105">Sign Up</a>
            </div>
            
            {/* Mobile menu button */}
            <div className="md:hidden flex items-center">
              <button 
                className="outline-none mobile-menu-button" 
                onClick={() => setMobileMenuOpen(!mobileMenuOpen)}
              >
                {mobileMenuOpen ? (
                  <X className="w-6 h-6 text-white" />
                ) : (
                  <Menu className="w-6 h-6 text-white" />
                )}
              </button>
            </div>
          </div>
        </div>
        
        {/* Mobile Menu */}
        <div className={`md:hidden bg-blue-800 transition-all duration-300 ease-in-out ${mobileMenuOpen ? 'max-h-64 opacity-100' : 'max-h-0 opacity-0 overflow-hidden'}`}>
          <a href="#" className="block py-2 px-4 text-blue-100 hover:bg-blue-700">Home</a>
          <a href="#" className="block py-2 px-4 text-blue-100 hover:bg-blue-700">About</a>
          <a href="#" className="block py-2 px-4 text-blue-100 hover:bg-blue-700">Contact</a>
          <a href="#faq" className="block py-2 px-4 text-blue-100 hover:bg-blue-700">FAQ</a>
          <div className="flex justify-between py-2 px-4">
            <a href="#" className="block py-2 px-3 bg-blue-700 hover:bg-blue-600 text-white rounded transition duration-300 w-1/2 mr-2 text-center">Login</a>
            <a href="#" className="block py-2 px-3 bg-blue-700 hover:bg-blue-600 text-white rounded transition duration-300 w-1/2 ml-2 text-center">Sign Up</a>
          </div>
        </div>
      </nav>
      
      {/* Hero Section */}
      <div 
        className="bg-gradient-to-r from-blue-800 to-indigo-700 pt-24 pb-16"
        style={{
          backgroundImage: `url('/api/placeholder/1200/400')`,
          backgroundSize: 'cover',
          backgroundPosition: 'center',
          backgroundBlendMode: 'overlay'
        }}
      >
        <div className="max-w-6xl mx-auto px-4 sm:px-6 lg:px-8 text-center">
          <h1 
            className="text-4xl md:text-5xl font-bold text-white mb-4 opacity-0 animate-fade-in-down"
            style={{animation: 'fadeInDown 1s ease-out forwards'}}
          >
            Journey to Inner Peace
          </h1>
          <p 
            className="text-xl text-blue-100 mb-8 opacity-0"
            style={{animation: 'fadeInUp 1s ease-out 0.5s forwards'}}
          >
            Discover ancient Indian wisdom for modern wellness
          </p>
          <button 
            className="bg-white text-blue-800 font-bold rounded-full py-3 px-8 shadow-lg hover:shadow-xl transition duration-300 transform hover:scale-110 opacity-0"
            style={{animation: 'fadeInUp 1s ease-out 1s forwards'}}
          >
            Begin Your Journey
          </button>
        </div>
      </div>
      
      {/* Main Category Cards */}
      <div className="max-w-6xl mx-auto px-4 py-16">
        <h2 className="text-3xl font-bold text-center text-gray-800 mb-12 transform transition-all duration-700" 
            style={{ 
              transform: scrollPosition > 100 ? 'translateY(0)' : 'translateY(20px)',
              opacity: scrollPosition > 100 ? 1 : 0 
            }}>
          Explore Our Offerings
        </h2>
        
        <div className="grid md:grid-cols-2 lg:grid-cols-4 gap-8">
          {/* Spiritual Music Card */}
          <div 
            className={`offering-card bg-white rounded-lg overflow-hidden shadow-md hover:shadow-xl transition duration-500 flex flex-col h-full border border-gray-100 transform ${animatedCards.includes(0) ? 'translate-y-0 opacity-100' : 'translate-y-10 opacity-0'}`}
            onMouseEnter={(e) => e.currentTarget.classList.add('scale-105')}
            onMouseLeave={(e) => e.currentTarget.classList.remove('scale-105')}
          >
            <div className="h-48 bg-blue-800 flex items-center justify-center overflow-hidden group">
              <Music size={64} className="text-white transition-all duration-500 group-hover:scale-125 group-hover:rotate-12" />
            </div>
            <div className="p-6 flex flex-col flex-grow">
              <h3 className="text-xl font-bold text-gray-800 mb-2">  (Spiritual Music)</h3>
              <p className="text-gray-600 mb-4 flex-grow">   ,         </p>
              <a href="/music" className="text-blue-700 hover:text-blue-900 font-semibold group inline-flex items-center">
                Explore Music 
                <span className="ml-1 transition-transform duration-300 group-hover:translate-x-2">&rarr;</span>
              </a>
            </div>
          </div>
          
          {/* Stories Card */}
          <div 
            className={`offering-card bg-white rounded-lg overflow-hidden shadow-md hover:shadow-xl transition duration-500 flex flex-col h-full border border-gray-100 transform ${animatedCards.includes(1) ? 'translate-y-0 opacity-100' : 'translate-y-10 opacity-0'}`}
            style={{ transitionDelay: '0.2s' }}
            onMouseEnter={(e) => e.currentTarget.classList.add('scale-105')}
            onMouseLeave={(e) => e.currentTarget.classList.remove('scale-105')}
          >
            <div className="h-48 bg-blue-700 flex items-center justify-center overflow-hidden group">
              <BookOpen size={64} className="text-white transition-all duration-500 group-hover:scale-125 group-hover:rotate-12" />
            </div>
            <div className="p-6 flex flex-col flex-grow">
              <h3 className="text-xl font-bold text-gray-800 mb-2">  (Spiritual Stories)</h3>
              <p className="text-gray-600 mb-4 flex-grow">,              </p>
              <a href="#" className="text-blue-700 hover:text-blue-900 font-semibold group inline-flex items-center">
                Read Stories 
                <span className="ml-1 transition-transform duration-300 group-hover:translate-x-2">&rarr;</span>
              </a>
            </div>
          </div>

          {/* Health Related Card */}
          <div 
            className={`offering-card bg-white rounded-lg overflow-hidden shadow-md hover:shadow-xl transition duration-500 flex flex-col h-full border border-gray-100 transform ${animatedCards.includes(2) ? 'translate-y-0 opacity-100' : 'translate-y-10 opacity-0'}`}
            style={{ transitionDelay: '0.4s' }}
            onMouseEnter={(e) => e.currentTarget.classList.add('scale-105')}
            onMouseLeave={(e) => e.currentTarget.classList.remove('scale-105')}
          >
            <div className="h-48 bg-blue-600 flex items-center justify-center overflow-hidden group">
              <Leaf size={64} className="text-white transition-all duration-500 group-hover:scale-125 group-hover:rotate-12" />
            </div>
            <div className="p-6 flex flex-col flex-grow">
              <h3 className="text-xl font-bold text-gray-800 mb-2">  (Ayurvedic Wellness)</h3>
              <p className="text-gray-600 mb-4 flex-grow">         </p>
              <a href="#" className="text-blue-700 hover:text-blue-900 font-semibold group inline-flex items-center">
                Discover Health 
                <span className="ml-1 transition-transform duration-300 group-hover:translate-x-2">&rarr;</span>
              </a>
            </div>
          </div>
          
          {/* Online Sessions Card */}
          <div 
            className={`offering-card bg-white rounded-lg overflow-hidden shadow-md hover:shadow-xl transition duration-500 flex flex-col h-full border border-gray-100 transform ${animatedCards.includes(3) ? 'translate-y-0 opacity-100' : 'translate-y-10 opacity-0'}`}
            style={{ transitionDelay: '0.6s' }}
            onMouseEnter={(e) => e.currentTarget.classList.add('scale-105')}
            onMouseLeave={(e) => e.currentTarget.classList.remove('scale-105')}
          >
            <div className="h-48 bg-blue-500 flex items-center justify-center overflow-hidden group">
              <Video size={64} className="text-white transition-all duration-500 group-hover:scale-125 group-hover:rotate-12" />
            </div>
            <div className="p-6 flex flex-col flex-grow">
              <h3 className="text-xl font-bold text-gray-800 mb-2">  (Online Sessions)</h3>
              <p className="text-gray-600 mb-4 flex-grow">     ,     </p>
              <a href="#" className="text-blue-700 hover:text-blue-900 font-semibold group inline-flex items-center">
                Join Sessions 
                <span className="ml-1 transition-transform duration-300 group-hover:translate-x-2">&rarr;</span>
              </a>
            </div>
          </div>
        </div>
      </div>
      
      {/* Featured Section */}
      <div className="bg-gradient-to-r from-slate-100 to-blue-100 py-16">
        <div className="max-w-6xl mx-auto px-4">
          <div className="flex flex-col md:flex-row items-center">
            <div className="md:w-1/2 mb-8 md:mb-0 md:pr-8 transform transition-all duration-700" 
                style={{ 
                  transform: scrollPosition > 500 ? 'translateX(0)' : 'translateX(-50px)',
                  opacity: scrollPosition > 500 ? 1 : 0 
                }}>
              <h2 className="text-3xl font-bold text-gray-800 mb-4">Upcoming Special Event</h2>
              <h3 className="text-xl text-blue-700 mb-4">Sacred Sound Healing Journey</h3>
              <p className="text-gray-700 mb-6">Join our expert sound healers for a transformative experience with traditional Indian instruments including singing bowls, flutes, and tanpura.</p>
              
              {/* Countdown Timer */}
              <div className="grid grid-cols-4 gap-2 mb-6">
                <div className="bg-white rounded-lg p-2 text-center shadow-md">
                  <div className="text-2xl font-bold text-blue-700">{timeRemaining.days}</div>
                  <div className="text-xs text-gray-500">DAYS</div>
                </div>
                <div className="bg-white rounded-lg p-2 text-center shadow-md">
                  <div className="text-2xl font-bold text-blue-700">{timeRemaining.hours}</div>
                  <div className="text-xs text-gray-500">HOURS</div>
                </div>
                <div className="bg-white rounded-lg p-2 text-center shadow-md">
                  <div className="text-2xl font-bold text-blue-700">{timeRemaining.minutes}</div>
                  <div className="text-xs text-gray-500">MINUTES</div>
                </div>
                <div className="bg-white rounded-lg p-2 text-center shadow-md">
                  <div className="text-2xl font-bold text-blue-700">{timeRemaining.seconds}</div>
                  <div className="text-xs text-gray-500">SECONDS</div>
                </div>
              </div>
              
              <div className="flex items-center text-gray-600 mb-2">
                <Clock className="h-5 w-5 mr-2 text-blue-700" />
                <span>Sunday, April 12th at 7:00 PM IST</span>
              </div>
              <div className="flex items-center text-gray-600 mb-6">
                <MapPin className="h-5 w-5 mr-2 text-blue-700" />
                <span>Online via Zoom</span>
              </div>
              <button className="bg-blue-700 hover:bg-blue-800 text-white font-bold py-2 px-6 rounded transition duration-300 transform hover:scale-105 hover:shadow-lg">
                Register Now
              </button>
            </div>
            <div className="md:w-1/2 transform transition-all duration-700" 
                style={{ 
                  transform: scrollPosition > 500 ? 'translateX(0)' : 'translateX(50px)',
                  opacity: scrollPosition > 500 ? 1 : 0 
                }}>
              <div className="bg-white p-2 rounded-lg shadow-lg overflow-hidden group">
                <img 
                  src="/api/placeholder/600/400" 
                  alt="Sound healing instruments" 
                  className="rounded transition-transform duration-700 group-hover:scale-105" 
                />
              </div>
            </div>
          </div>
        </div>
      </div>
      
      {/* Testimonials */}
      <div className="max-w-6xl mx-auto px-4 py-16">
        <h2 className="text-3xl font-bold text-center text-gray-800 mb-12 transform transition-all duration-700" 
            style={{ 
              transform: scrollPosition > 800 ? 'translateY(0)' : 'translateY(20px)',
              opacity: scrollPosition > 800 ? 1 : 0 
            }}>
          What Our Community Says
        </h2>
        
        {/* Testimonial Carousel */}
        <div className="relative overflow-hidden">
          <div 
            className="flex transition-transform duration-500 ease-in-out"
            style={{ transform: `translateX(-${activeTestimonial * 100}%)` }}
          >
            {testimonials.map((testimonial, index) => (
              <div 
                key={index} 
                className="w-full flex-shrink-0 px-4"
              >
                <div className="bg-white p-8 rounded-lg shadow-md border border-gray-100 h-full">
                  <div className="flex items-center mb-4">
                    <div className="h-12 w-12 rounded-full bg-blue-100 flex items-center justify-center mr-4">
                      <span className="text-blue-700 font-bold text-xl">{testimonial.initial}</span>
                    </div>
                    <div>
                      <h4 className="font-bold">{testimonial.name}</h4>
                      <p className="text-gray-600">{testimonial.location}</p>
                    </div>
                  </div>
                  <p className="text-gray-700">{testimonial.text}</p>
                </div>
              </div>
            ))}
          </div>
          
          {/* Carousel Navigation */}
          <div className="flex justify-center mt-6">
            {testimonials.map((_, index) => (
              <button
                key={index}
                className={`h-3 w-3 mx-1 rounded-full ${activeTestimonial === index ? 'bg-blue-700' : 'bg-gray-300'}`}
                onClick={() => setActiveTestimonial(index)}
              />
            ))}
          </div>
          
          {/* Carousel Controls */}
          <button 
            className="absolute top-1/2 left-0 transform -translate-y-1/2 bg-white p-2 rounded-full shadow-lg text-blue-700 hover:text-blue-900 hover:bg-gray-100 transition duration-300"
            onClick={() => setActiveTestimonial((prev) => (prev - 1 + testimonials.length) % testimonials.length)}
          >
            <ChevronUp className="rotate-90" />
          </button>
          <button 
            className="absolute top-1/2 right-0 transform -translate-y-1/2 bg-white p-2 rounded-full shadow-lg text-blue-700 hover:text-blue-900 hover:bg-gray-100 transition duration-300"
            onClick={() => setActiveTestimonial((prev) => (prev + 1) % testimonials.length)}
          >
            <ChevronDown className="-rotate-90" />
          </button>
        </div>
      </div>

      {/* FAQ Section */}
      <div id="faq" className="max-w-4xl mx-auto px-4 py-16">
        <h2 className="text-3xl font-bold text-center text-gray-800 mb-12">Frequently Asked Questions</h2>
        
        <div className="space-y-4">
          {faqItems.map((item, index) => (
            <div 
              key={index}
              className="bg-white rounded-lg shadow-md overflow-hidden transition-all duration-300"
            >
              <button 
                className="flex justify-between items-center w-full p-4 text-left font-semibold text-gray-800 hover:bg-gray-50 focus:outline-none"
                onClick={() => toggleAccordion(index)}
              >
                <span>{item.question}</span>
                {openAccordion === index ? (
                  <ChevronUp className="h-5 w-5 text-blue-700" />
                ) : (
                  <ChevronDown className="h-5 w-5 text-blue-700" />
                )}
              </button>
              <div 
                className={`px-4 overflow-hidden transition-all duration-300 ${
                  openAccordion === index ? 'max-h-40 pb-4' : 'max-h-0'
                }`}
              >
                <p className="text-gray-600">{item.answer}</p>
              </div>
            </div>
          ))}
        </div>
      </div>
      
      {/* Newsletter */}
      <div className="bg-blue-900 py-16">
        <div className="max-w-4xl mx-auto px-4 text-center">
          <h2 className="text-3xl font-bold text-white mb-4">Join Our Spiritual Community</h2>
          <p className="text-blue-100 mb-8">Subscribe to receive weekly wisdom, event notifications and exclusive content</p>
          
          <div className="flex flex-col sm:flex-row sm:justify-center">
            <div className="flex items-center bg-white rounded-l-md px-3 py-3 sm:w-96">
              <Mail className="h-5 w-5 text-blue-700 mr-2" />
              <input 
                className="w-full focus:outline-none text-gray-700" 
                type="email" 
                placeholder="Your email address"
              />
            </div>
            <button className="bg-blue-700 hover:bg-blue-800 text-white px-6 py-3 sm:rounded-l-none rounded-md sm:rounded-r-md font-semibold transition duration-300 mt-2 sm:mt-0 transform hover:scale-105">
              Subscribe
            </button>
          </div>
        </div>
      </div>

      {/* Custom CSS for Animations */}
      <style jsx>{`
        @keyframes fadeInDown {
          from {
            opacity: 0;
            transform: translateY(-20px);
          }
          to {
            opacity: 1;
            transform: translateY(0);
          }
        }
        
        @keyframes fadeInUp {
          from {
            opacity: 0;
            transform: translateY(20px);
          }
          to {
            opacity: 1;
            transform: translateY(0);
          }
        }
        
        .animate-fade-in-down {
          animation: fadeInDown 1s ease-out forwards;
        }
        
        .animate-fade-in-up {
          animation: fadeInUp 1s ease-out forwards;
        }
        
        .animate-pulse {
          animation: pulse 2s infinite;
        }
        
        @keyframes pulse {
          0%, 100% {
            opacity: 1;
          }
          50% {
            opacity: 0.6;
          }
        }
      `}</style>
    </div>
  );
};

export default SpiritualWellnessPage;
