import React, { useState } from 'react';

const App = () => {
  const [currentPage, setCurrentPage] = useState('storiesList');
  const [selectedStory, setSelectedStory] = useState(null);

  // Sample stories data in Hindi with images
  const stories = [
    {
      id: 1,
      title: "अहंकार का अंत",
      summary: "एक राजा के अहंकार और उसके द्वारा सीखे गए सबक की कहानी",
