import React, { useState, useRef, useEffect } from 'react';
import { Play, Pause, SkipBack, SkipForward, Search, Music } from 'lucide-react';

// Mock database of bhajans
const bhajansDatabase = [
  {
    id: 1,
    title: "ॐ जय जगदीश हरे",
    artist: "Traditional",
    audioUrl: "/audio/song1.mp3",
    duration: 300,
    lyrics: [
      "ॐ जय जगदीश हरे",
      "स्वामी जय जगदीश हरे",
      "भक्त जनों के संकट, दास जनों के संकट",
      "क्षण में दूर करे॥",
    ]
  },
  {
    id: 2,
    title: "हनुमान चालीसा",
    artist: "Traditional",
    audioUrl: "/audio/song2.mp3",
    duration: 480,
    lyrics: [
      "श्री गुरु चरण सरोज रज",
      "निज मनु मुकुरु सुधारि",
      "बरनऊं रघुबर बिमल जसु",
      "जो दायकु फल चारि"
    ]
  },
  {
    id: 3,
    title: "गायत्री मंत्र",
    artist: "Traditional",
    audioUrl: "/audio/song3.mp3",
    duration: 240,
    lyrics: [
      "ॐ भूर्भुवः स्वः",
      "तत्सवितुर्वरेण्यं",
      "भर्गो देवस्य धीमहि",
      "धियो यो नः प्रचोदयात्"
    ]
  }
];

const SpiritualMusicPlayer = () => {
  const [isPlaying, setIsPlaying] = useState(false);
  const [currentTime, setCurrentTime] = useState(0);
  const [songs, setSongs] = useState(bhajansDatabase);
  const [currentSongIndex, setCurrentSongIndex] = useState(0);
  const [searchQuery, setSearchQuery] = useState('');
  const [currentLyricIndex, setCurrentLyricIndex] = useState(0);
  const [error, setError] = useState(null);
  
  const audioRef = useRef(null);
  const currentSong = songs[currentSongIndex];

  // Handle next/previous - Defined before being used in useEffect
  const handleNext = () => {
    setCurrentSongIndex((prev) => (prev + 1) % songs.length);
  };

  const handlePrevious = () => {
    setCurrentSongIndex((prev) => (prev - 1 + songs.length) % songs.length);
  };

  // Initialize audio on mount
  useEffect(() => {
    if (!audioRef.current) {
      audioRef.current = new Audio();
    }

    const audio = audioRef.current;
    
    setError(null);
    
    const handleError = (e) => {
      console.error('Audio error:', e);
      setError('Unable to play this song. Please try another.');
      setIsPlaying(false);
    };

    const handleEnded = () => {
      setIsPlaying(false);
      handleNext();
    };

    audio.addEventListener('error', handleError);
    audio.addEventListener('ended', handleEnded);

    audio.src = currentSong.audioUrl;
    audio.load();

    return () => {
      audio.removeEventListener('error', handleError);
      audio.removeEventListener('ended', handleEnded);
      audio.pause();
    };
  }, [currentSong.audioUrl, handleNext]); // Added missing dependencies

  // Handle song change
  useEffect(() => {
    const audio = audioRef.current;
    if (!audio) return;

    setError(null);
    const wasPlaying = !audio.paused;
    
    audio.src = currentSong.audioUrl;
    audio.load();
    
    if (wasPlaying) {
      audio.play().catch(error => {
        console.error("Playback failed:", error);
        setError('Unable to play this song. Please try another.');
        setIsPlaying(false);
      });
    } else {
      setIsPlaying(false);
    }
    
    setCurrentTime(0);
    setCurrentLyricIndex(0);
  }, [currentSong.audioUrl]);

  // Handle play/pause
  useEffect(() => {
    const audio = audioRef.current;
    if (!audio) return;

    if (isPlaying) {
      audio.play().catch(error => {
        console.error("Playback failed:", error);
        setError('Unable to play this song. Please try another.');
        setIsPlaying(false);
      });
    } else {
      audio.pause();
    }
  }, [isPlaying]);

  // Handle search
  const handleSearch = (query) => {
    setSearchQuery(query);
    if (query.trim() === '') {
      setSongs(bhajansDatabase);
      setCurrentSongIndex(0);
    } else {
      const filtered = bhajansDatabase.filter(song => 
        song.title.toLowerCase().includes(query.toLowerCase()) ||
        song.artist.toLowerCase().includes(query.toLowerCase())
      );
      setSongs(filtered);
      setCurrentSongIndex(0);
    }
  };

  // Handle seek
  const handleSeek = (e) => {
    const audio = audioRef.current;
    if (!audio) return;

    const rect = e.target.getBoundingClientRect();
    const x = e.clientX - rect.left;
    const percentage = x / rect.width;
    const newTime = percentage * currentSong.duration;
    audio.currentTime = newTime;
    setCurrentTime(newTime);
  };

  return (
    <div className="max-w-4xl mx-auto p-6 bg-blue-50 min-h-screen">
      {/* Search Section */}
      <div className="mb-8">
        <h1 className="text-3xl font-bold text-blue-800 mb-4">भजन प्लेयर</h1>
        <div className="flex gap-4">
          <input 
            type="text"
            value={searchQuery}
            onChange={(e) => handleSearch(e.target.value)}
            placeholder="भजन खोजें..." 
            className="flex-1 text-lg p-6 border border-blue-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
          />
          <button className="bg-blue-600 text-white p-4 rounded-lg hover:bg-blue-700 transition-colors">
            <Search size={24} />
          </button>
        </div>
      </div>

      {error && (
        <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded relative mb-4">
          <span className="block sm:inline">{error}</span>
        </div>
      )}

      {/* Player Card */}
      <div className="bg-white rounded-xl shadow-xl p-6">
        {/* Song Info */}
        <div className="flex items-center gap-4 mb-6">
          <div className="bg-blue-100 p-4 rounded-full">
            <Music className="w-12 h-12 text-blue-600" />
          </div>
          <div>
            <h2 className="text-2xl font-bold text-blue-800">{currentSong.title}</h2>
            <p className="text-blue-600">{currentSong.artist}</p>
          </div>
        </div>

        {/* Progress Bar */}
        <div className="mb-6">
          <div 
            className="relative h-3 bg-blue-100 rounded-full cursor-pointer"
            onClick={handleSeek}
          >
            <div 
              className="absolute top-0 left-0 h-full bg-blue-600 rounded-full transition-all duration-300 ease-in-out"
              style={{ width: `${(currentTime / currentSong.duration) * 100}%` }}
            />
          </div>
          <div className="flex justify-between mt-2 text-blue-600 font-medium">
            <span>{Math.floor(currentTime / 60)}:{String(Math.floor(currentTime % 60)).padStart(2, '0')}</span>
            <span>{Math.floor(currentSong.duration / 60)}:{String(currentSong.duration % 60).padStart(2, '0')}</span>
          </div>
        </div>

        {/* Controls */}
        <div className="flex justify-center items-center gap-8">
          <button 
            className="p-3 text-blue-600 hover:text-blue-800 transition-colors"
            onClick={handlePrevious}
          >
            <SkipBack size={36} />
          </button>
          <button 
            className="p-4 bg-blue-600 text-white rounded-full hover:bg-blue-700 transition-colors transform hover:scale-105"
            onClick={() => setIsPlaying(!isPlaying)}
          >
            {isPlaying ? <Pause size={48} /> : <Play size={48} />}
          </button>
          <button 
            className="p-3 text-blue-600 hover:text-blue-800 transition-colors"
            onClick={handleNext}
          >
            <SkipForward size={36} />
          </button>
        </div>

        {/* Lyrics Section */}
        <div className="mt-8 p-4 bg-blue-50 rounded-lg">
          <h3 className="text-xl font-semibold text-blue-800 mb-4">बोल:</h3>
          <div className="space-y-2">
            {currentSong.lyrics.map((line, index) => (
              <p 
                key={index} 
                className={`text-lg leading-relaxed p-2 rounded transition-colors ${
                  index === currentLyricIndex 
                    ? 'bg-blue-100 text-blue-800' 
                    : 'text-blue-700'
                }`}
              >
                {line}
              </p>
            ))}
          </div>
        </div>

        {/* Playlist Section */}
        <div className="mt-6 border-t border-blue-100 pt-4">
          <h3 className="text-lg font-semibold text-blue-800 mb-2">सभी भजन:</h3>
          <div className="space-y-2">
            {songs.map((song, index) => (
              <div 
                key={song.id}
                onClick={() => setCurrentSongIndex(index)}
                className={`flex items-center p-2 rounded-lg cursor-pointer ${
                  currentSongIndex === index 
                    ? 'bg-blue-100' 
                    : 'hover:bg-blue-50'
                }`}
              >
                <Music className="w-5 h-5 text-blue-600 mr-3" />
                <span className="text-blue-700">{song.title}</span>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default SpiritualMusicPlayer;
