import { useParams, useNavigate } from 'react-router-dom';
import { useEffect, useState } from 'react';
import bhajansData from '../assets/lyrics/bhajans.json';

const MusicPage = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [bhajan, setBhajan] = useState(null);
  const [isPlaying, setIsPlaying] = useState(false);
  const [audio, setAudio] = useState(null);

  useEffect(() => {
    // Find the bhajan by ID
    const data = Array.isArray(bhajansData) ? bhajansData : bhajansData?.bhajans || [];
    const foundBhajan = data.find(b => b.id === parseInt(id));
    
    if (!foundBhajan) {
      navigate('/'); // Redirect if bhajan not found
      return;
    }

    setBhajan(foundBhajan);
    
    // Initialize audio
    const newAudio = new Audio(foundBhajan.audio);
    setAudio(newAudio);

    return () => {
      if (newAudio) {
        newAudio.pause();
      }
    };
  }, [id, navigate]);

  const togglePlay = () => {
    if (!audio) return;

    if (isPlaying) {
      audio.pause();
    } else {
      audio.play();
    }
    setIsPlaying(!isPlaying);
  };

  if (!bhajan) {
    return (
      <div className="min-h-screen bg-blue-50 flex items-center justify-center">
        <div className="text-blue-800">Loading bhajan...</div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-b from-blue-50 to-blue-100 py-8 px-4 sm:px-6 lg:px-8">
      <div className="max-w-2xl mx-auto">
        <button 
          onClick={() => navigate(-1)}
          className="mb-6 px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors"
        >
          Back to Bhajans
        </button>

        <div className="bg-white rounded-lg shadow-lg p-6">
          <h1 className="text-3xl font-bold text-blue-800 mb-4">{bhajan.title}</h1>
          
          <div className="mb-6">
            <pre className="text-blue-900 whitespace-pre-wrap font-sans">{bhajan.lyrics}</pre>
          </div>
          
          <div className="flex items-center gap-4 mb-6">
            <button
              onClick={togglePlay}
              className={`px-6 py-3 rounded-full ${isPlaying ? 'bg-blue-700' : 'bg-blue-600'} text-white`}
            >
              {isPlaying ? 'Pause' : 'Play'}
            </button>
            <div className="flex gap-2">
              <span className="bg-blue-200 text-blue-800 px-3 py-1 rounded-full text-sm">
                {bhajan.deity}
              </span>
              <span className="bg-blue-100 text-blue-700 px-3 py-1 rounded-full text-sm">
                {bhajan.duration}
              </span>
            </div>
          </div>

          <div className="mt-4">
            <h2 className="text-xl font-semibold text-blue-800 mb-2">About this Bhajan</h2>
            <p className="text-blue-700">
              This {bhajan.language} bhajan is dedicated to {bhajan.deity}. 
              The divine melodies help in spiritual awakening and inner peace.
            </p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default MusicPage;
