import { useState, useEffect } from 'react';

const BhajanPage = () => {
  const [bhajans, setBhajans] = useState([
    {
      id: 1,
      title: "Hare Krishna Hare Rama",
      lyrics: "Hare Krishna, Hare Krishna\nKrishna Krishna, Hare Hare\nHare Rama, Hare Rama\nRama Rama, Hare Hare",
      deity: "Krishna",
      language: "Sanskrit",
      duration: "4:30"
    },
    {
      id: 2,
      title: "Shiva Tandava Stotram",
      lyrics: "Jatatavigalajjala pravahapavitasthale\nGaleavalambya lambitam bhujangatungamalikam...",
      deity: "Shiva",
      language: "Sanskrit",
      duration: "8:15"
    },
    {
      id: 3,
      title: "Hanuman Chalisa",
      lyrics: "Shri Guru Charan Saroj Raj\nNij Man Mukar Sudhari\nBarnau Raghubar Bimal Jasu\nJo Dayaku Phal Chari...",
      deity: "Hanuman",
      language: "Awadhi",
      duration: "7:00"
    },
    {
      id: 4,
      title: "Om Jai Jagdish Hare",
      lyrics: "Om Jai Jagdish Hare\nSwami Jai Jagdish Hare\nBhakt Jano Ke Sankat\nDaas Jano Ke Sankat\nKshat Mein Door Kare...",
      deity: "Vishnu",
      language: "Hindi",
      duration: "5:45"
    },
    {
      id: 5,
      title: "Aigiri Nandini",
      lyrics: "Aigiri Nandini Nandhitha Medhini\nVishwa Vinodhini Nandanuthe\nJaya Jaya He Mahishasura Mardhini...",
      deity: "Durga",
      language: "Sanskrit",
      duration: "6:30"
    },
    {
      id: 6,
      title: "Jai Ganesh Jai Ganesh Deva",
      lyrics: "Jai Ganesh Jai Ganesh Jai Ganesh Deva\nMata Jaki Parvati Pita Mahadeva...",
      deity: "Ganesha",
      language: "Hindi",
      duration: "3:45"
    },
    {
      id: 7,
      title: "Vaishnav Jan To",
      lyrics: "Vaishnav jan to tene kahiye\nJe peerh paraayi jaaney re\nPar dukhe upkar kare toye...",
      deity: "Vishnu",
      language: "Gujarati",
      duration: "4:15"
    },
    {
      id: 8,
      title: "Shri Ram Chandra Kripalu Bhajman",
      lyrics: "Shri Ram Chandra Kripalu Bhajman\nHarana Bhavabhaya Daarunam...",
      deity: "Ram",
      language: "Awadhi",
      duration: "5:20"
    },
    {
      id: 9,
      title: "Radhe Krishna Radhe Krishna",
      lyrics: "Radhe Krishna Radhe Krishna\nKrishna Krishna Radhe Radhe\nRadhe Shyam Radhe Shyam\nShyam Shyam Radhe Radhe",
      deity: "Krishna",
      language: "Sanskrit",
      duration: "4:50"
    },
    {
      id: 10,
      title: "Om Namah Shivaya",
      lyrics: "Om Namah Shivaya\nOm Namah Shivaya\nShivaya Namah Om\nNamah Shivaya",
      deity: "Shiva",
      language: "Sanskrit",
      duration: "3:30"
    },
    {
      id: 11,
      title: "Jai Ambe Gauri",
      lyrics: "Jai Ambe Gauri Maiya\nJai Shyama Gauri\nNishidin Tumko Dhyavat\nHar Brahmandeshwari...",
      deity: "Durga",
      language: "Hindi",
      duration: "6:15"
    },
    {
      id: 12,
      title: "Shri Krishna Govind Hare Murari",
      lyrics: "Shri Krishna Govind Hare Murari\nHey Nath Narayan Vasudeva...",
      deity: "Krishna",
      language: "Sanskrit",
      duration: "5:10"
    },
    {
      id: 13,
      title: "Saraswati Vandana",
      lyrics: "Yaa Kundendu Tushaara Haara Dhavalaa\nYaa Shubhra Vastraavritaa\nYaa Veena Varadanda Manditakaraa...",
      deity: "Saraswati",
      language: "Sanskrit",
      duration: "4:40"
    },
    {
      id: 14,
      title: "Hey Nath Narayan Vasudeva",
      lyrics: "Hey Nath Narayan Vasudeva\nHari Hari Govind Hare Murari...",
      deity: "Krishna",
      language: "Hindi",
      duration: "4:25"
    },
    {
      id: 15,
      title: "Shri Ram Jai Ram Jai Jai Ram",
      lyrics: "Shri Ram Jai Ram Jai Jai Ram\nOm Shri Ram Jai Ram Jai Jai Ram...",
      deity: "Ram",
      language: "Sanskrit",
      duration: "3:55"
    }
  ]);

  const [searchTerm, setSearchTerm] = useState("");
  const [selectedDeity, setSelectedDeity] = useState("All");
  const [expandedBhajan, setExpandedBhajan] = useState(null);

  const deities = ["All", ...new Set(bhajans.map(bhajan => bhajan.deity))];

  const filteredBhajans = bhajans.filter(bhajan => {
    const matchesSearch = bhajan.title.toLowerCase().includes(searchTerm.toLowerCase()) || 
                         bhajan.lyrics.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesDeity = selectedDeity === "All" || bhajan.deity === selectedDeity;
    return matchesSearch && matchesDeity;
  });

  const toggleExpand = (id) => {
    setExpandedBhajan(expandedBhajan === id ? null : id);
  };

  return (
    <div className="min-h-screen bg-gradient-to-b from-amber-50 to-amber-100 py-8 px-4 sm:px-6 lg:px-8">
      <div className="max-w-4xl mx-auto">
        <div className="text-center mb-12">
          <h1 className="text-4xl font-bold text-amber-800 mb-2">Bhajan Sangrah</h1>
          <p className="text-lg text-amber-700">Discover divine melodies for spiritual upliftment</p>
        </div>

        <div className="bg-white rounded-lg shadow-lg p-6 mb-8">
          <div className="flex flex-col sm:flex-row gap-4 mb-6">
            <div className="flex-1">
              <label htmlFor="search" className="block text-sm font-medium text-amber-800 mb-1">Search Bhajans</label>
              <input
                type="text"
                id="search"
                placeholder="Search by title or lyrics..."
                className="w-full px-4 py-2 border border-amber-300 rounded-lg focus:ring-2 focus:ring-amber-500 focus:border-amber-500"
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
              />
            </div>
            <div className="w-full sm:w-48">
              <label htmlFor="deity" className="block text-sm font-medium text-amber-800 mb-1">Filter by Deity</label>
              <select
                id="deity"
                className="w-full px-4 py-2 border border-amber-300 rounded-lg focus:ring-2 focus:ring-amber-500 focus:border-amber-500"
                value={selectedDeity}
                onChange={(e) => setSelectedDeity(e.target.value)}
              >
                {deities.map(deity => (
                  <option key={deity} value={deity}>{deity}</option>
                ))}
              </select>
            </div>
          </div>

          <div className="space-y-6">
            {filteredBhajans.length > 0 ? (
              filteredBhajans.map(bhajan => (
                <div key={bhajan.id} className="border border-amber-200 rounded-lg overflow-hidden">
                  <div 
                    className="bg-amber-50 px-4 py-3 flex justify-between items-center cursor-pointer"
                    onClick={() => toggleExpand(bhajan.id)}
                  >
                    <div>
                      <h3 className="text-lg font-semibold text-amber-900">{bhajan.title}</h3>
                      <div className="flex gap-3 mt-1">
                        <span className="text-xs bg-amber-200 text-amber-800 px-2 py-1 rounded-full">{bhajan.deity}</span>
                        <span className="text-xs bg-amber-100 text-amber-700 px-2 py-1 rounded-full">{bhajan.language}</span>
                        <span className="text-xs bg-amber-100 text-amber-700 px-2 py-1 rounded-full">{bhajan.duration}</span>
                      </div>
                    </div>
                    <svg 
                      className={`w-5 h-5 text-amber-600 transform transition-transform ${expandedBhajan === bhajan.id ? 'rotate-180' : ''}`} 
                      fill="none" 
                      viewBox="0 0 24 24" 
                      stroke="currentColor"
                    >
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 9l-7 7-7-7" />
                    </svg>
                  </div>
                  {expandedBhajan === bhajan.id && (
                    <div className="bg-white p-4">
                      <pre className="text-amber-900 whitespace-pre-wrap font-sans">{bhajan.lyrics}</pre>
                      <button className="mt-3 px-4 py-2 bg-amber-600 text-white rounded-lg hover:bg-amber-700 transition-colors">
                        Play Bhajan
                      </button>
                    </div>
                  )}
                </div>
              ))
            ) : (
              <div className="text-center py-8">
                <p className="text-amber-700">No bhajans found matching your criteria.</p>
              </div>
            )}
          </div>
        </div>

        <div className="text-center text-amber-700">
          <p>May these divine melodies bring peace to your heart and mind.</p>
        </div>
      </div>
    </div>
  );
};

export default BhajanPage;
